import React, {useState} from 'react';
import ReactDOM from 'react-dom';

const Header = (props) => {
    return <h1>{props.header}</h1> 
}

const Result = (props) => {
    return <p>{props.result}</p>
}

const App = () => {
    // tallenna napit omaan tilaansa
    const header = 'anna palautetta'
    const statistiikka = 'statistiikka'

    const [good, setGood] = useState(0)
    const [neutral, setNeutral] = useState(0)
    const [bad, setBad] = useState(0)

    const klikkaus = (who) => {
        const handler = () => console.log('klikkaus', who)
        return handler
    }

    const setToGood = (newValue) => {
        setGood(newValue)
    }

    const setToNeutral = (newValue) => {
        setNeutral(newValue)
    }

    const setToBad = (newValue) => {
        setBad(newValue)
    }

    return (
        <div>
            <Header header={header} />

            <button onClick={() => setToGood(good + 1)}>hyv√§</button>
            <button onClick={() => setToNeutral(neutral + 1)}>neutraali</button>
            <button onClick={() => setToBad(bad + 1)}>huono</button>

            <Header header={statistiikka} />
            <Result result={good} />
            <Result result={neutral} />
            <Result result={bad} />

        </div>
    )
}

ReactDOM.render(<App />, document.getElementById('root'));
